import altair as altimport pandas as pdimport datetimesource = pd.read_csv("house.csv")print(source.head())# select year 2012source = source.loc[source['year'] == 2012,]fig = (       alt.Chart(source)       .mark_line(point=True)       .encode(           x = "date",           y = "daily_consumption",           color = 'House',           #order = 'year',           tooltip=['date', 'House', 'daily_consumption'],           )       )fig.save("ALTAIR.html")