import plotly.express as pximport pandas as pdimport os import globimport csvhouse4 = pd.read_csv("house4.csv")df = house4.loc[house4['year'] == 2012,]df['month'] = pd.to_numeric(df['month'])df = df.sort_values("month")print(df.head())fig = px.bar(df, x="Category", y="value", color="Category",  animation_frame="month", range_y=[0,100000])fig.show()fig.write_html("PLOTLY.html")